p_email:
let procedure p_email(v_name :String) := {
  now => @com.google.puppeteer(id="com.google.puppeteer").load(url="http://localhost:3000/"^^tt:url);
  now => @com.google.puppeteer(id="com.google.puppeteer").set_input(text="hello " + v_name, frame_url="http://localhost:3000/"^^tt:url, selector="body > textarea");
  now => @com.google.puppeteer(id="com.google.puppeteer").click(frame_url="http://localhost:3000/"^^tt:url, selector="body > #send");
} ;
